package com.target.mbe.gardening.tools;

import com.target.mbe.gardening.tools.api.ApiConfiguration;
import com.target.mbe.gardening.tools.api.error.InternalErrorException;
import com.target.mbe.gardening.tools.api.service.sqlite.SqlitePersistence;
import java.io.IOException;
import java.net.URI;
import org.glassfish.grizzly.http.server.HttpServer;
import org.glassfish.jersey.grizzly2.httpserver.GrizzlyHttpServerFactory;
import org.glassfish.jersey.jackson.JacksonFeature;
import org.glassfish.jersey.server.ResourceConfig;

public class App
{

    // Base URI the Grizzly HTTP server will listen on
    public static final String BASE_URI = "http://localhost:8080/api/";

    /**
     * Starts Grizzly HTTP server exposing JAX-RS resources defined in this application.
     *
     * @return Grizzly HTTP server.
     */
    public static HttpServer startServer()
    {
        final ResourceConfig rc = ApiConfiguration.getConfiguration();

        // create and start a new instance of grizzly http server
        // exposing the Jersey application at BASE_URI
        return GrizzlyHttpServerFactory.createHttpServer(URI.create(BASE_URI), rc);
    }

    /**
     * Main method.
     */
    public static void main(String[] args) throws IOException
    {
        insertSamples();

        final HttpServer server = startServer();
        System.out.println(String.format("Jersey app started with WADL available at "
            + "%sapplication.wadl\nHit enter to stop it...", BASE_URI));
        System.in.read();
        server.shutdownNow();
    }

    public static void insertSamples()
    {
        SqlitePersistence db = new SqlitePersistence();
        try
        {
            db.createTool("axe", "red", 5, "");
            db.createTool("wheelbarrow", "blue", 3, "premium");
            db.createTool("dibber", "brown", 50, "golden");
        }
        catch (InternalErrorException e)
        {
            e.printStackTrace();
        }
    }
}